// 1. Obtener todas las órdenes del body
const items = $input.first().json.body;
const validatedOrders = [];
const allErrors = [];

// Función genérica de validación
function validateField(field, type = "string", minLength = 0) {
  if (field === undefined || field === null) return false;
  if (type === "string") {
    return typeof field === "string" && field.trim().length >= minLength;
  }
  if (type === "number") return typeof field === "number" && !isNaN(field);
  if (type === "integer") return Number.isInteger(field);
  if (type === "array") return Array.isArray(field) && field.length > 0;
  return true;
}

// 2. Procesar cada orden del array
for (const order of items) {
  const errors = [];

  if (!validateField(order.customerName, "string", 1)) errors.push(`Nombre inválido en ID ${order.id}`);

  if (!validateField(order.deliveryAddress, "string", 1)) errors.push(`Dirección de entrega invalida en ID ${order.id}`);

  // Validación de teléfono: string y longitud >= 8
  if (!validateField(order.phoneNumber, "string", 8)) errors.push(`Teléfono inválido (mínimo 8 caracteres) en ID ${order.id}`);

  if (!validateField(order.id, "integer") || order.id <= 0) errors.push(`ID inválido: ${order.id}`);

  // Validación de items dentro de la orden
  if (!validateField(order.orderItems, "array")) {
    errors.push(`Orden ${order.id} no tiene items`);
  } else {
    order.orderItems.forEach((item, index) => {
      if (!validateField(item.foodName, "string", 1)) errors.push(`Item ${index} sin nombre en ID ${order.id}`);
      if (!validateField(item.quantity, "number") || item.quantity <= 0) errors.push(`Cantidad inválida en ID ${order.id}`);
    });
  }

  // 3. Clasificar resultados
  if (errors.length > 0) {
    allErrors.push({ orderId: order.id, details: errors });
  } else {
    validatedOrders.push(order);
  }
}

// 4. Retornar un objeto consolidado
return [{
  json: {
    valid: allErrors.length === 0,
    totalProcessed: items.length,
    order: allErrors.length === 0 ? validatedOrders : null, // Esto permite que no envie datos si existe algun error de validadcion
    errors: allErrors
  }
}];

// 1. Obtener las órdenes validadas del nodo anterior
const ordenes = $json.order || [];

// 2. Inicializar contadores
let metrics = {
  pendingCount: 0,
  processingCount: 0,
  completedCount: 0,
  canceledCount: 0,
  totalAmount: 0,
  totalClients: ordenes.length
};

// 3. Frecuencia de productos
const foodFrequency = {};

// 4. Procesar el array
for (const order of ordenes) {
  // Sumar montos de forma segura (asegura que sea número)
  if(order.status === "COMPLETED") {
  metrics.totalAmount += Number(order.totalAmount || 0);
    }

  // Contar estados (Normalizamos a mayúsculas por si acaso)
  const status = (order.status || '').toUpperCase();
  if (status === "PENDING") metrics.pendingCount++;
  else if (status === "PROCESSING") metrics.processingCount++;
  else if (status === "COMPLETED") metrics.completedCount++;
  else if (status === "CANCELLED" || status === "CANCELED") metrics.canceledCount++;

  // Analizar ítems
  if (order.orderItems && Array.isArray(order.orderItems)) {
    for (const item of order.orderItems) {
      const food = item.foodName;
      if (food) {
        foodFrequency[food] = (foodFrequency[food] || 0) + 1;
      }
    }
  }
}

// 5. Encontrar el plato más vendido
let mostRepeatedFood = "N/A";
let maxCount = 0;
for (const [food, count] of Object.entries(foodFrequency)) {
  if (count > maxCount) {
    maxCount = count;
    mostRepeatedFood = food;
  }
}

// 6. Formatear moneda para el reporte
const formattedAmount = new Intl.NumberFormat('es-ES', {
  style: 'currency',
  currency: 'USD'
}).format(metrics.totalAmount);

// 7. Retorno final
return [{
  json: {
    ...metrics,
    totalAmount: formattedAmount, // Enviamos el string bonito para el email
    mostRepeatedFood
  }
}];

const errores = $json.errors || [];
const totalProcessed = $json.totalProcessed || 0;

// Timestamp limpio: AAAA-MM-DD HH:mm:ss
const fechaReporte = new Date().toLocaleString();

if (errores.length === 0) {
  return [{
    json: {
      orderId: "N/A",
      detalles: "Sin errores detectados",
      fecha: fechaReporte,
      cantidadErrores: 0,
      totalProcesados: totalProcessed
    }
  }];
}

// Mapeo detallado
return errores.map(error => {
  // Forzamos el conteo real del array de detalles
  const numErrores = Array.isArray(error.details) ? error.details.length : 0;

  return {
    json: {
      orderId: error.orderId,
      // Usamos un separador que no rompa el CSV (punto y coma o pipe)
      detalles: error.details.map((d, i) => `${i + 1}. ${d}`).join(' | '), 
      fecha: fechaReporte,
      cantidadErrores: numErrores, // Aquí verás el "4" correctamente
      totalProcesados: totalProcessed
    }
  };
});

